insert into Dictionary values ("1","en","Information"," ","General information about the database DesInventar with the intention of providing information about the same."," "); 
insert into Dictionary values ("2","en","Id database","No duplicates","Identifier of Database"," "); 
insert into Dictionary values ("3","en","Database","Name of the database","Label or name of the database"," "); /*3-DB-RegionLabel*/
insert into Dictionary values ("4","en","Description","Text or key words","General information about the database, which should contain: \n-Period\n-Number of reports\n-Sources of information\n-General description of database."," "); /*4-DB-RegionDesc*/
insert into Dictionary values ("5","en","English description","Text or key words","Translation of the text from “Descripción” to English."," "); /*5-DB-RegionDescEN*/
insert into Dictionary values ("6","en","Language","Select an item from the list","Default language in which to enter information into the database."," "); /*6-DB-RegionLangCode*/
insert into Dictionary values ("7","en","Last revised","Date of the last revision of the database.","Date on which the basic information of the Region was last modified."," "); /*7-DB-RegionStructLastUpdate*/
insert into Dictionary values ("8","en","Start date","Input date in YYYY-MM-DD format"," "," "); /*8-DB-PeriodBeginDate*/
insert into Dictionary values ("9","en","End date","Input date in YYYY-MM-DD format"," "," "); /*9-DB-PeriodEndDate*/
insert into Dictionary values ("10","en","Administrator","Email address","Email address of the administrator of the Database"," "); /*10-DB-OptionAdminEMail*/
insert into Dictionary values ("11","en","Restricted?","Select or deselect this option","This option enables limitation of the start and end dates of the database."," "); /*11-DB-OptionOutOfPeriod*/
insert into Dictionary values ("12","en","MinLong","Decimal degrees","Minimum longitude coordinates (left) of the geographic region. Use as reference the system WGS84."," "); /*12-DB-LimitMinX*/
insert into Dictionary values ("13","en","MinLat","Decimal degrees","Minimum latitude coordinates (bottom) of the geographic region. Use as reference the system WGS84."," "); /*13-DB-LimitMinY*/
insert into Dictionary values ("14","en","MaxLong","Decimal degrees","Maximum longitude coordinates (right) of the geographic region. Use as reference the system WGS84."," "); /*14-DB-LimitMaxX*/
insert into Dictionary values ("15","en","MaxLat","Decimal degrees","Maximum latitude coordinates (top) of the geographic region. Use as reference the system WGS84."," "); /*15-DB-LimitMaxY*/
insert into Dictionary values ("16","en","Authorization"," ","Authorization determines the access and role that the users of the database will have. Authorization may be added or edited here."," "); /*16-DB-Permissions*/
insert into Dictionary values ("17","en","Users","Select an item from the list","Names of the people who have some role in the database."," "); /*17-DB-UserName*/
insert into Dictionary values ("18","en","Roles","Select an item from the list","Determines the level of access of a user, according to 4 roles: \n-Feeder\n-Observer\n-Supervisor\n-Administrator"," "); /*18-DB-Role*/
insert into Dictionary values ("19","en","Administrator"," ","The Administrator has access to the structural configuration of the database and the records. He/she may assign roles, assign descriptions in the database logs, edit lists of events, causes and geography."," "); /*19-DB-RoleAdmin*/
insert into Dictionary values ("20","en","Observer"," ","The Observer may see the entire structure of the database in read-only mode. This role enables external users to see the region during the construction process of the database. "," "); /*20-DB-RoleObserver*/
insert into Dictionary values ("21","en","Supervisor"," ","The Supervisor has access to the disaster records and may authorise the publication and deletion of records."," "); /*21-DB-RoleSupervisor*/
insert into Dictionary values ("22","en","Feeder"," ","The Feeder may access disaster records in order to create and update data."," "); /*22-DB-RoleUser*/
insert into Dictionary values ("23","en","None"," ","Deactivate all roles for this User."," "); /*23-DB-RoleNone*/
insert into Dictionary values ("24","en","Log"," ","History of the construction of the database. Contains description of the credits, methodology (additional criteria of the user), landmarks and maintenance"," "); /*24-DB-Log*/
insert into Dictionary values ("25","en","Type","Select an item from the list","The types of logs available are: credits, methodology, landmarks and maintenance."," "); /*25-DB-LogType*/
insert into Dictionary values ("26","en","Content","Text or key words","Description of the log."," "); /*26-DB-LogNote*/
insert into Dictionary values ("27","en","Credits","Select an option","People and institutions who are authors or co-authors of the database."," "); /*27-DB-LogCredits*/
insert into Dictionary values ("28","en","Methodology","Select an option","Additional criteria of the DesInventar methodology taken into account by the user of the database (eg. lists of magnitude, references to equivalent geographical effects, personalised events)."," "); /*28-DB-LogMethodology*/
insert into Dictionary values ("29","en","Landmarks","Select an option","Occurrence that affects the interpretation of the database analysis (eg, currency change, hyperinflation, socio-political conflicts). This should include temporal information about the occurrence."," "); /*29-DB-LogStaff*/
insert into Dictionary values ("30","en","Maintenance","Select an option","General matters to do with maintenance and updating of the database: revision, quality control tests, changes to the structure of the database."," "); /*30-DB-LogSupport*/
insert into Dictionary values ("31","en","Administration of geography"," ","Management of the geographic fields: levels and elements."," "); /*31-DB-Geograph*/
insert into Dictionary values ("32","en","Levels","Input or edit a geographic level","Geographic levels, by hierarchical order. The use of political-administrative divisions is suggested. Example: Highest level – State; following level: Province."," "); /*32-DB-GeoLev*/
insert into Dictionary values ("33","en","Name","Input a name – max 20 characters","Name of the geographic level."," "); /*33-DB-LevName*/
insert into Dictionary values ("34","en","Description ","Text or key words","Text describing the level. This may include synonyms used in the region or in past times."," "); /*34-DB-LevDesc*/
insert into Dictionary values ("35","en","Map?","Select or deselect this option","If there is a map for this level, select this option in order to input the necessary data to associate the map in the Database."," "); /*35-DB-LevHaveMap*/
insert into Dictionary values ("36","en","Map Name","Input a name","Name of the layer of the map in the database which contains the geographic elements. In Shape format, this is the name of the file (without the extension)."," "); /*36-DB-LevLayerFile*/
insert into Dictionary values ("37","en","Column Code","Input a value","Header of the column which contains the code for the geographic elements."," "); /*37-DB-LevLayerCode*/
insert into Dictionary values ("38","en","Column Name","Input a value","Header of the column which contains the name of the geographic elements."," "); /*38-DB-LevLayerName*/
insert into Dictionary values ("39","en","Elements","Input or edit a geographic element","List of geographic items which comprise each level."," "); /*39-DB-GeoEle*/
insert into Dictionary values ("40","en","Code","Input a value","Official code or code used commonly in the region. This should correspond to cartography."," "); /*40-DB-EleCode*/
insert into Dictionary values ("41","en","Name","Input text","Official name or name commonly used in the region. This name should correspond to that assigned in associated cartography."," "); /*41-DB-EleName*/
insert into Dictionary values ("42","en","Parent","Select an element from the list, if applicable","Element of the highest level to which the current geographical element belongs."," "); /*42-DB-EleParent*/
insert into Dictionary values ("43","en","Activate","Select or deselect this option","Indicates whether the element present will be visible or not in the lists. This may occur because the geographic element has been detached and currently does not actually exist."," "); /*43-DB-EleActive*/
insert into Dictionary values ("44","en","Administration of events"," ","Management of the predefined event fields by DesInventar and personalised by the database."," "); /*44-DB-Event*/
insert into Dictionary values ("45","en","Predefined event","Input a name","Name of the predefined event. Only the Administrator of the database may edit this name in order to approximate the best-known local terminology, always taking into account the definition. "," "); /*45-DB-EvePredefName*/
insert into Dictionary values ("46","en","Personalised event","Input a name","Personalised name of the event. Only the Administrator of the database may create this type of event. These events may only be consulted in the database where they were created."," "); /*46-DB-EvePersonName*/
insert into Dictionary values ("47","en","Definition","Text or key words","Descriptive text of the official DesInventar event for the base. In contrast to the name, the description may not be altered by the Administrator of the database. This is in order to guarantee comparability between databases."," "); /*47-DB-EvePredefDef*/
insert into Dictionary values ("48","en","Definition","Text or key words","Text describing the event."," "); /*48-DB-EvePersonDef*/
insert into Dictionary values ("49","en","Active","Select or deselect this option","Indicates whether the event will be visible in the basic Record or not. This is useful where the event is not applicable in the database."," "); /*49-DB-EveActive*/
insert into Dictionary values ("50","en","Administration of Causes"," ","Management of the predefined Cause fields by DesInventar and personalised in the database."," "); /*50-DB-Cause*/
insert into Dictionary values ("51","en","Predefined Cause","Input a name","Name of the predefined cause. Only the Administrator of the database may edit the name of the Cause in order to approximate the best-known local terminology, always taking into account the definition."," "); /*51-DB-CauPredefName*/
insert into Dictionary values ("52","en","Personalised cause","Input a name","Name of the Personalised Cause. Only the Administrator of the database may create a new Cause. These Causes may only be consulted in the database where they were created."," "); /*52-DB-CauPersonName*/
insert into Dictionary values ("53","en","Definition","Text or key words","Descriptive text of the Predefined Cause. In contrast to the name, the definition may not be altered. This is in order to guarantee comparability between databases."," "); /*53-DB-CauPredefDef*/
insert into Dictionary values ("54","en","Definition","Text or key words","Descriptive text of the Cause."," "); /*54-DB-CauPersonDef*/
insert into Dictionary values ("55","en","Active","Select or deselect this option","This indicates whether the Cause will be visible or not in the basic Record. This may be useful in cases where the cause is not applicable in the database."," "); /*55-DB-CauActive*/
insert into Dictionary values ("56","en","Administration of Additional Effects"," ","Management of the Additional Effects fields and Groups of Effects."," "); /*56-DB-ExtraEffect*/
insert into Dictionary values ("57","en","Label","Input a name","Visible name of the Additional Effect"," "); /*57-DB-EEFieldLabel*/
insert into Dictionary values ("58","en","Definition","Text or key words","Description of the Additional Effect field."," "); /*58-DB-EEFieldDesc*/
insert into Dictionary values ("59","en","Type","Select from the list","Type of data to be consigned."," "); /*59-DB-EEFieldType*/
insert into Dictionary values ("60","en","Size","Numerical value","Maximum field length."," "); /*60-DB-EEFieldSize*/
insert into Dictionary values ("61","en","Order","Numerical value","Visualisation order"," "); /*61-DB-EEFieldOrder*/
insert into Dictionary values ("62","en","Active","Select or deselect this option","Indicates whether this is active. If deactivated, users will not be able to see this in Module DesInventar."," "); /*62-DB-EEFieldActive*/
insert into Dictionary values ("63","en","Public","Select or deselect this option","Indicates whether the field is for public consultation in Module DesConsultar module."," "); /*63-DB-EEFieldPublic*/
insert into Dictionary values ("68","en","Datacard importation"," ","Datacard importation let insert disaster's registers to DesInventar in masive way. Require a file in CSV format and each datacard must have a unique serial, a date, geography unit, datasource, a event and a cause."," "); /*68-DB-Import*/
insert into Dictionary values ("100","en","Serial","No duplicates. Max 40 characters","Number which identifies each record. Symbols/Acronyms/Abbreviations  or codes may be used. Use of sequential numbering is recommended. Letters, dashes and other characters may be used. The identifier or Serial number is unique for every record.","Before starting input of information into your inventory, design a strategy for the identification of the serial numbers. There are various options depending on your needs. Here are two possibilities:<br><br>Use the first digits of the serial to identify the year in which the disaster took place and the following digits to specify a number which identifies each record. For example 1978-1200 would refer to the registered number 1200 of the year 1978. If the inventory you are constructing is for a national system which works in the prevention of and attention to disasters, which would work in zones, the Serial number may include a code which identifies the serial as belonging to a particular operating zone. For example, the operative zone in the west of the country may register a record as WES -1200, or, WES-1978-1200, where WES is the code which identifies the western zone of the country and 1200 corresponds to the serial.<br><br>If your institution maintains a paper register (forms) or a digital register (photos, reports) of the disaster data, you may add the Serial to these documents in order to facilitate later searches for information.<br><br>This numbering is independent of the internal coding of the registers in the database, and can therefore be manipulated according to the needs of the inventory."); /*100-Disaster-Serial*/
insert into Dictionary values ("101","en","Start date","Input date in YYYY-MM-DD format","In this field, the year, month and date of the occurrence of the disaster is registered.","Although the intention is to gather information for the three fields: year, month and day, this is not always possible. In some cases it may not be easy to determine the day exactly (or even the month) when a flood epidemic or plague started. In these cases, it is recommended that all available sources are gone through to determine the month; otherwise, input only the year."); /*101-Disaster-BeginTime*/
insert into Dictionary values ("102","en","Sources","Formatted text","Sources of information used to obtain the data registered in the Record. One or more sources may be included, depending on the case. These may be: one or more media information sources, pre-existing databases and the organisation or organisations from which the information was obtained.","The registering of sources of information must be rigorous, as much for historic inventories based on files and institutional sources, as for those inventories being developed in quasi-real time by national or local institutions or risk management. It is recommended to use abbreviations for each source and to use a decreasing order according to the quality and credibility of each one.  In this way, it is easier to evaluate the inventories and to later ask questions and analyse the databases. For the same reasons, it is important that the data be homogeneous, which includes the same spelling, abbreviations, type of letters (upper case or lower case) and symbols."); /*102-Disaster-Source*/
insert into Dictionary values ("103","en","Geography","Select from the lists","The geographic fields relate to the resolution and geographic levels of the inventory. They determine and verify the location of the disaster (in terms of the established levels) before starting data entry.","Use the source of information itself, but consult, wherever possible, official cartography in order to verify that the location corresponds to correct geographical units. You will find that many of the errors (eg, a town or county which does not belong to a particular state) can be detected by DesInventar. In other cases, especially where newspaper sources are used, the detailed resolution is not available because there is no certainty about which of these levels was affected. For this reason, the subordinate geographic levels are not strictly obligatory. However, it is expected that the lowest geographic level which corresponds to the resolution in the database will be registered. Otherwise, it would be assumed that the disaster occurred in all the elements (geographic units) of the higher level, which would, of course negatively influence the quality of the database."); /*103-Disaster-GeographyId*/
insert into Dictionary values ("104","en","Place","Descriptive text","Specific place in which the disaster occurred. If the Place name is the same as the lowest of the geographic levels reported, it is not necessary to re-input this. In the case of rural disasters, Place can be a location, a village, a river, a territory which covers various geographic units, a farm or country house, a geographic accident, etc. In the case of urban disasters, Place should include the address and/or neighbourhood.","If the Place name is the same as the lowest of the geographic levels reported, it is not necessary to re-input this. In the case of rural disasters, Place can be a location, a village, a river, a territory which covers various geographic units, a farm or country house, a geographic accident, etc. In the case of urban disasters, Place should include the address and/or neighbourhood."); /*104-Disaster-SiteNotes*/
insert into Dictionary values ("105","en","Latitude","Decimal degrees","Latitude of the specific place where the disaster occurred. Use as reference the system WGS84."," "); /*105-Disaster-Latitude*/
insert into Dictionary values ("106","en","Longitude","Decimal degrees","Longitude of the specific place where the disaster occurred. Use as reference the system WGS84."," "); /*106-Disaster-Longitude*/
insert into Dictionary values ("107","en","Save As","Select an option","Indicates the future availability of the record, facilitating the process of quality control of the database. Choose an option to save this record."," "); /*107-Record|1-Status*/
insert into Dictionary values ("108","en","Author"," ","User who created the record."," "); /*108-Record|2-Author*/
insert into Dictionary values ("109","en","Creation date"," ","Date of the creation of the record."," "); /*109-Record|2-Creation*/
insert into Dictionary values ("110","en","Updated"," ","Latest date on which the record was updated."," "); /*110-Record|2-LastUpdate*/
insert into Dictionary values ("111","en","Type of event","Select from the list","The Event is a phenomenon – natural, socio-natural or technological – which acts as a detonator of adverse effects on human lives, health and/or the economic and social infrastructure of a community. Remember that in DesInventar an Event is different from a disaster. In contrast to specialised institutions such as meteorological and seismological services, in DesInventar not all events are included, only those events that have associated adverse effects (for example, rainfall without adverse effects would not be included, tremors of low magnitude and intensity that do not cause damage, floods in regions that are unoccupied or uncultivated that do not cause damage, etc).","DesInventar has a predefined list of 36 types of Events. The selection of terms to denominate these types of Events is not “orthodox”, as seen from disciplines such as geology, meteorology, etc, but rather is an attempt to arrive at the most common designations for the different phenomena that can affect countries. The selection process relied on the consultation of diverse dictionaries and technical glossaries (which by no means should be seen as exhaustive), along with discussions in international workshops with research participants from social, economic and natural physics sciences. The intention is to provide a gathering of terms which are as homogeneous as possible in different countries and  which may be useful for projects whose sources of information are not always specialised."); /*111-Event-Id*/
insert into Dictionary values ("112","en","Duration","Number of days","The number of days an Event lasted. The majority of events may last a few minutes (ie, earthquakes, landslides, flashfloods) or hours (ie, flooding or fires). Events such as droughts or prolonged flooding may last months.","The majority of events may last a few minutes (ie, earthquakes, landslides, flashfloods) or hours (ie, flooding or fires). Events such as droughts or prolonged flooding may last months."); /*112-Event-Duration*/
insert into Dictionary values ("113","en","Magnitude","Value of magnitude","The magnitude of the Event. In this field internationally-used values of magnitude are registered (ie, the Richter scale for earthquakes, the Saffir Simpson scale for hurricanes); quantified variables (wind speed, area of landslide, cubic centimetres of earth fallen, value of rainfall, etc) or brief descriptions such as – light, medium, high, extreme – etc.","This corresponds to internationally-used values of magnitude for seismic events, volcanic eruptions, tsunami, hurricanes or tropical storms. For other types of events, the user may include quantified variables such as wind speed, height in metres of torrential flows or floods, amount of rainfall in a place in a 24 hour period, volume area of a landslide or deposit. The same units should be used for every type of variable: km/hr, m, mm/day, m2, m3, km3. If a work team has (or develops) a methodology to measure the magnitude of the disaster, it is recommended that they create a field in the section Additional Effects."); /*113-Event-Magnitude*/
insert into Dictionary values ("114","en","Observation about the event","Text or key words","A brief description of the occurrence of the event. In the case of chain events or simultaneous events, these should also be included in the description."," "); /*114-Event-Notes*/
insert into Dictionary values ("115","en","Type of cause","Select from the list","The phenomenon which immediately caused the event. Normally disasters are caused by a combination of circumstances relating to the socio-cultural, economic, political and historical environment, which may combine with an event to detonate the disaster, whether this is a phenomenon of natural or anthropological origin.","The theme of multi-causality of disasters is still an area for academic and conceptual debate, and one which DesInventar does not intend to cover. One of the objectives of DesInventar’s methodology, and precisely one of the motives for its development, is to validate or improve hypotheses about the relationship between disasters and conditions of vulnerability.  For that reason, it should try to facilitate the relation between the inventories and other social, economic or environmental variables. In this sense, a group of causes has been predefined whose application is not always evident or direct. In some cases, the cause is attributed to phenomena of wider spatial or energy coverage (earthquakes, hurricanes, etc.) which would thus encapsulate other, obviously fundamental, variables such as planning and use of soils, overcrowding and deterioration of life conditions for large sections of the population, etc. It is recommended that the use of the causes – Other Cause – and – Unknown – be avoided, without first revising carefully the list of causes. Then, depending on necessity and context, a cause can be created."); /*115-Cause-Id*/
insert into Dictionary values ("116","en","Observations about the cause","Text or key words","A brief annotation of the relationship between the cause and the event. This field is a very important in terms of analysis of the database because here would be described those causes which complement or contextualise the one registered in the field Cause.","Strictly speaking every disaster, however natural it may appear, occurs because of an imbalance between complex variables and dynamics of which a social environment or one of infrastructure is constructed - services and internal factors (vulnerable conditions) as much as external factors (the event of natural or anthropological origin) which bring to light the risk and lead to the event taking place. This field is included in order to register all those variables and relations to the causality of the event. For example, differences in the determination of the cause of an event, such as whether a landslide is due solely to rainfall or because of inappropriate management of a sewage system. "); /*116-Cause-Notes*/
insert into Dictionary values ("117","en","Deaths","Type a number or select from the list","The number of persons whose deaths were directly caused. When final official data is available, this figure should be included with corresponding observations, for example, when there are differences between officially accepted figures and those of other sources. Suppositions about number of deaths, not officially verified, should be registered in the field - Observations of Effects – including the source of information."," "); /*117-Effect|PeopleGood-PeopleDead*/
insert into Dictionary values ("118","en","Affected","Type a number or select from the list","The number of persons who suffer indirect or secondary effects related to a disaster.  This refers to the number of people, distinct from victims, who suffer the impact of secondary effects of disasters for such reasons as deficiencies in public services, commerce, work, or because of isolation. If the information refers to families, calculate the number of people according to available indicators."," "); /*118-Effect|PeopleGood-PeopleAffected*/
insert into Dictionary values ("119","en","Evacuees","Type a number or select from the list","The number of persons temporarily evacuated from their homes, work places, schools, hospitals, etc. If the information refers to families, calculate the number of people according to available indicators."," "); /*119-Effect|PeopleGood-PeopleEvacuated*/
insert into Dictionary values ("120","en","Missing","Type a number or select from the list","The number of persons whose whereabouts since the disaster is unknown. It includes people who are presumed dead, although there is no physical evidence.  The data on number of deaths and number of missing are mutually exclusive and should not be mixed."," "); /*120-Effect|PeopleGood-PeopleMissing*/
insert into Dictionary values ("121","en","Relocated","Type a number or select from the list","The number of persons who have been moved permanently from their homes to new sites. If the information refers to families, calculate the number of people according to available indicators."," "); /*121-Effect|PeopleGood-PeopleRelocated*/
insert into Dictionary values ("122","en","Victims","Type a number or select from the list","The number of persons whose goods and/or individual or collective services have suffered serious damage, directly associated with the event. For example, partial or total destruction of their homes and goods; loss of crops and/or crops stored in warehouses, etc. If the information refers to families, calculate the number of people according to available indicators."," "); /*122-Effect|PeopleGood-PeopleHarmed*/
insert into Dictionary values ("123","en","Wounded, sick","Type a number or select from the list","The number of persons whose health or physical integrity is affected as a direct result of the disaster. This figure does not include victims who die.  Those who suffer injuries and or illness, if the event is related to a plague or epidemic, should be included here."," "); /*123-Effect|PeopleGood-PeopleInjured*/
insert into Dictionary values ("124","en","Homes affected","Type a number or select from the list","The number of homes with minor damage, not structural or architectural, which may continue being lived in, although they may require some repair or cleaning."," "); /*124-Effect|PeopleGood-HousesAffected*/
insert into Dictionary values ("125","en","Homes destroyed","Type a number or select from the list","The number of homes levelled, buried, collapsed or damaged to the extent that they are no longer habitable."," "); /*125-Effect|PeopleGood-HousesDestroyed*/
insert into Dictionary values ("126","en","Loss value $","Amount in local currency","Sum of losses directly caused by the disaster in local currency."," "); /*126-Effect|Economic-LossesValueLocal*/
insert into Dictionary values ("127","en","Loss value US$","Amount in US dollars.","The equivalent in dollars (US$) of the value of losses in local currency, according to the exchange rate on the date of the disaster. This figure is useful for comparative evaluations between databases."," "); /*127-Effect|Economic-LossesValueUSD*/
insert into Dictionary values ("128","en","Health Sector","Quantity","The number of health centres, clinics, local and regional hospitals destroyed and directly or indirectly affected by the disaster."," "); /*128-Effect|Affected|SectorHealth-MedicalCenters*/
insert into Dictionary values ("129","en","Educational centres","Quantity","The amount of play schools, kindergartens, schools, colleges, universities, training centres etc, destroyed or directly or indirectly affected by the disaster. Include those that have been used as temporary shelters."," "); /*129-Effect|Affected|SectorEducation-EducationCenters*/
insert into Dictionary values ("130","en","Crops and woods (Hectares)","Quantity","The amount of cultivated or pastoral land or woods destroyed or affected. If the information exists in another measurement, it should be converted to hectares."," "); /*130-Effect|Affected|SectorAgricultural-FarmingAndForest*/
insert into Dictionary values ("131","en","Livestock","Quantity","The number of animals lost (bovine, pig, ovine, poultry) regardless of the type of event (flood, drought, epidemic, etc)."," "); /*131-Effect|Affected|SectorAgricultural-LiveStock*/
insert into Dictionary values ("132","en","Routes affected","Quantity","The length of transport networks destroyed and/or rendered unusable, in metres."," "); /*132-Effect|Affected|SectorTransport-Roads*/
insert into Dictionary values ("133","en","Other losses","Text","A description of other losses not included in the fields of the basic record. For example: religious buildings and monuments, architectural or cultural heritage buildings, theatres and public installations, public administration buildings relating to banks, commerce and tourism; vehicles or buses lost, bridges.","A calculated or estimated value of losses due to indirect or induced effects attributable to interruptions or deficiencies in public services, trade, work or geographic isolation. If the value of these losses is not available, those effects that are not found in the fields of the record may be noted here. It is also possible to include the calculation of cost of repair or reconstruction of the damages caused by the disaster."); /*133-Effect|More|SectorOther-OtherLosses*/
insert into Dictionary values ("134","en","Observations about the effects","Text","Notes or detail about the effects of the disaster. This field should be used to register discrepancies between effect figures when there is more than one source.","In accordance, with the sequence of fields in the Basic Record, it is recommended to use the following codes for observations (first type the code and then the corresponding observation): R. Relocated, T. Transport, Vd. Homes destroyed, Ac. Aqueduct, Va. Homes Affected, En. Energy, V. Routes, I. Industry, A. Agriculture, Al. Sewerage, P. Fishing, C. Communications, S. Health, Ma. Magnitude, Ed. Education, Fe. Date of the end of an event which covers a long period. Vp. Loss Value. "); /*134-Effect|More-Notes*/
insert into Dictionary values ("135","en","Transport","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to the effects of the disaster on the transport sector: road networks (train or rail), transport stations, airports, river and sea bridges, sea walls, etc, that have been affected or destroyed."," "); /*135-Sector-Transport*/
insert into Dictionary values ("136","en","Communications","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to damages to the communication sector: plants and telephone networks, radio and television stations, post offices and public information offices, internet services, radio telephones and mobile phones. "," "); /*136-Sector-Communications*/
insert into Dictionary values ("137","en","Aid organisation installations","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to damages to the emergency response sector, specifically to the aid organisms’ installations: Fire fighters and other aid organisms and to entities of public order."," "); /*137-Sector-Relief*/
insert into Dictionary values ("138","en","Agriculture and fishing","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to damaged to the Agriculture and Fishing sector: crops, granaries, pastoral zones."," "); /*138-Sector-Agricultural*/
insert into Dictionary values ("139","en","Aqueduct","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to damages to the aqueduct sector: water outlets, water treatment plants, aqueducts and canals which carry drinking water, storage tanks."," "); /*139-Sector-WaterSupply*/
insert into Dictionary values ("140","en","Sewerage","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to damages to the sewage sector: sewage systems and treatment plants."," "); /*140-Sector-Sewerage*/
insert into Dictionary values ("141","en","Education","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to damages to the Education sector: everything relating to this sector – pre-schools, kindergartens, schools, colleges, universities, training centres, libraries, cultural centres, etc."," "); /*141-Sector-Education*/
insert into Dictionary values ("142","en","Energy","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to damages to the Energy sector: dams, substations, transmission lines, generators, energy processing plants and combustible stores, pipelines, gas lines, nuclear plants."," "); /*142-Sector-Power*/
insert into Dictionary values ("143","en","Industry","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to damages to the Industrial sector: all types and sizes of industry, including agricultural and fishing plants."," "); /*143-Sector-Industry*/
insert into Dictionary values ("144","en","Health","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to damages to the Health sector: everything related to the health sector, including communication networks, emergency networks (ambulances), casualty centres, etc."," "); /*144-Sector-Health*/
insert into Dictionary values ("145","en","Other","Choose an option","Qualitative field in the database. It has two options: Affected or Not Affected. It relates to damages which do not correspond to any of the fields in the basic record."," "); /*145-Sector-Other*/
insert into Dictionary values ("146","en","Draft","Choose an option","This relates to one of the storage states of the record: Record being edited, still incomplete (responsibility of the Feeder)."," "); /*146-RecordStatus-Draft*/
insert into Dictionary values ("147","en","For approval","Choose an option","This relates to one of the storage states of the record: Record ready to be approved (by the supervisor)."," "); /*147-RecordStatus-Aprobe*/
insert into Dictionary values ("148","en","Delete","Choose an option","This relates to one of the storage states of the record: Record is ready to be deleted (by the supervisor)."," "); /*148-RecordStatus-Trash*/
insert into Dictionary values ("149","en","Published","Choose an option","This relates to one of the storage states of the record: Consultation record published. It has been approved by the Supervisor for publication."," "); /*149-RecordStatus-Publish*/
insert into Dictionary values ("150","en","Deleted!","Choose an option","This relates to one of the storage states of the record: Record has been deleted by the Supervisor."," "); /*150-RecordStatus-Delete*/
