insert into Dictionary values ("25","es","Tipo","Seleccione un elemento de la lista","Los tipos de bitácora disponibles son: créditos, metodología, hito y mantenimiento."," "); /*25-DB-LogType*/
insert into Dictionary values ("26","es","Contenido","Texto o palabras claves","Descripción de la bitácora."," "); /*26-DB-LogNote*/
insert into Dictionary values ("27","es","Créditos","Elija una opción","Personas e instituciones autores o coautoras de la base de datos."," "); /*27-DB-LogCredits*/
insert into Dictionary values ("28","es","Metodología","Elija una opción","Criterios adicionales a la metodología de DesInventar asumidos por el usuario en la base de datos. (p.e. listados de magnitudes, referencias de equivalencias en efectos o geografía, eventos personalizados)."," "); /*28-DB-LogMethodology*/
insert into Dictionary values ("29","es","Hito","Elija una opción","Acontecimiento que afecta la interpretación del análisis de la base de datos (p.e. cambio de moneda, hiperinflación, conflictos sociopolíticos). Debe incluir información temporal del acontecimiento. "," "); /*29-DB-LogStaff*/
insert into Dictionary values ("30","es","Mantenimiento","Elija una opción","Asuntos generales del mantenimiento y actualización de la base de datos: revisión, pruebas de control de calidad, cambios en la estructura de la base."," "); /*30-DB-LogSupport*/
insert into Dictionary values ("31","es","Administración de geografía"," ","Gestión de los campos de geografía: niveles y elementos."," "); /*31-DB-Geograph*/
insert into Dictionary values ("32","es","Niveles","Ingrese o edite un nivel geográfico","Niveles geográficos, según orden jerárquico. Se sugiere el uso de la división politico-administrativa. Ejemplo: Nivel superior: Departamento, siguiente nivel: Provincia."," "); /*32-DB-GeoLev*/
insert into Dictionary values ("33","es","Nombre","Ingrese un nombre – max 20 caracteres","Nombre de los niveles geográficos."," "); /*33-DB-LevName*/
insert into Dictionary values ("34","es","Descripción","Texto o palabras claves","Texto con la descripción del nivel. Puede incluir sinónimos utilizados en la misma región o en épocas pasadas."," "); /*34-DB-LevDesc*/
insert into Dictionary values ("35","es","¿Tiene mapa?","Marque o desmarque esta opción","Si posee un mapa para este nivel, marque esta opción para ingresar los datos necesarios para asociarla a la Base de Datos."," "); /*35-DB-LevHaveMap*/
insert into Dictionary values ("36","es","Nombre Mapa","Ingrese un nombre","Nombre de la capa o Layer del mapa de la base de datos que contiene los elementos geográficos. En formato Shape, es el nombre del archivo (sin la extensión)."," "); /*36-DB-LevLayerFile*/
insert into Dictionary values ("37","es","Columna Código","Ingrese un valor","Encabezado de la columna que contiene el código de los elementos geográficos"," "); /*37-DB-LevLayerCode*/
insert into Dictionary values ("38","es","Columna Nombre","Ingrese un valor","Encabezado de la columna que contiene el nombre de los elementos geográficos"," "); /*38-DB-LevLayerName*/
insert into Dictionary values ("39","es","Elementos","Ingrese o edite un elemento geográfico","Listado de unidades geográficas que componen cada nivel."," "); /*39-DB-GeoEle*/
insert into Dictionary values ("40","es","Código","Ingrese un valor","Código oficial o comúnmente utilizado en la región. Debe corresponder con la cartografía."," "); /*40-DB-EleCode*/
insert into Dictionary values ("41","es","Nombre","Ingrese un texto","Nombre oficial o comunmente utilizado en la región. Este nombre debe corresponder al asignado en la cartografía asociada."," "); /*41-DB-EleName*/
insert into Dictionary values ("42","es","Padre","Seleccione un elemento de la lista, si el caso aplica","Elemento del nivel superior al cual pertenece el actual elemento geográfico."," "); /*42-DB-EleParent*/
insert into Dictionary values ("43","es","Activar","Marque o desmarque esta opción","Indica si el presente elemento estará disponible o nó para visualizarse en los listados. Esto puede ocurrir porque el elemento geográfico se desagregó y en la actualidad no existe como tal."," "); /*43-DB-EleActive*/
insert into Dictionary values ("44","es","Administración de eventos"," ","Gestión de los campos de eventos predefinidos por DesInventar y personalizados de la base"," "); /*44-DB-Event*/
insert into Dictionary values ("45","es","Evento predefinido","Ingrese un nombre","Nombre del evento predefinido. Sólo el administrador de la base de datos podrá editar este nombre para asemejarlo al término local más reconocido siempre que sea fiel a la definición."," "); /*45-DB-EvePredefName*/
insert into Dictionary values ("46","es","Evento personalizado","Ingrese un nombre","Nombre del evento personalizado. Sólo el administrador de la base de datos podrá crear estos eventos. Estos eventos sólo podrán ser consultados en la base de datos donde se crearon. "," "); /*46-DB-EvePersonName*/
insert into Dictionary values ("47","es","Definición","Texto o palabras claves","Texto descriptivo del evento oficial de DesInventar para la base. A diferencia del nombre, la descripción no podrá ser alterada por el Administrador de la base de datos, con el fin de garantizar la comparabilidad entre bases de datos."," "); /*47-DB-EvePredefDef*/
insert into Dictionary values ("48","es","Definición","Texto o palabras claves","Texto descriptivo del evento."," "); /*48-DB-EvePersonDef*/
insert into Dictionary values ("49","es","Activo","Marque o desmarque esta opción","Indica si el evento estará disponible o nó para visualizarse en la Ficha básica. Esto ser útil para los casos donde el evento no tiene aplicabilidad en la base de datos."," "); /*49-DB-EveActive*/
insert into Dictionary values ("50","es","Administración de causas"," ","Gestión de los campos de causas predefinidas por DesInventar y personalizadas de la base de datos"," "); /*50-DB-Cause*/
insert into Dictionary values ("51","es","Causa predefinida","Ingrese un nombre","Nombre de la causa predefinida. Sólo el administrador de la base de datos podrá editar el nombre de la causa para asemejarlo al término local más reconocido siempre que coincida con la definición."," "); /*51-DB-CauPredefName*/
insert into Dictionary values ("52","es","Causa personalizada","Ingrese un nombre","Nombre la causa personalizada. Sólo el administrador de la base de datos podrá crear una causa. Estas causas sólo podrán ser consultadas en la base de datos donde se crearon. "," "); /*52-DB-CauPersonName*/
insert into Dictionary values ("53","es","Definición","Texto o palabras claves","Texto descriptivo de la causa predefinida. A diferencia del nombre, la definición no podrá ser alterada con el fin de garantizar la comparabilidad entre bases de datos."," "); /*53-DB-CauPredefDef*/
insert into Dictionary values ("54","es","Definición","Texto o palabras claves","Texto descriptivo de la causa."," "); /*54-DB-CauPersonDef*/
insert into Dictionary values ("55","es","Activa","Marque o desmarque esta opción","Indica si la causa estará disponible o nó para visualizarse en la Ficha básica. Esto puede ser útil en los casos donde la causa no tenga aplicabilidad para la base de datos."," "); /*55-DB-CauActive*/
insert into Dictionary values ("56","es","Administración de efectos adicionales"," ","Gestión de los campos de Efectos Adicionales y Grupos de Efectos"," "); /*56-DB-ExtraEffect*/
insert into Dictionary values ("57","es","Etiqueta","Ingrese un nombre","Nombre visible del efecto adicional"," "); /*57-DB-EEFieldLabel*/
insert into Dictionary values ("58","es","Definición","Texto o palabras claves","Descripción del campo del efecto adicional"," "); /*58-DB-EEFieldDesc*/
insert into Dictionary values ("59","es","Tipo","Seleccione de la lista","Tipo de dato a consignar"," "); /*59-DB-EEFieldType*/
insert into Dictionary values ("60","es","Tamaño","Valor numérico","Longitud máxima del campo"," "); /*60-DB-EEFieldSize*/
insert into Dictionary values ("61","es","Orden","Valor numérico","Orden de visualización"," "); /*61-DB-EEFieldOrder*/
insert into Dictionary values ("62","es","Activo","Marque o desmarque esta opción","Indica si se encuentra activo. Si se desactiva, los usuarios ya no podrán visualizarlo en los módulos DesInventar."," "); /*62-DB-EEFieldActive*/
insert into Dictionary values ("63","es","Público","Marque o desmarque esta opción","Indica si el campo será de consulta pública por el módulo DesConsultar"," "); /*63-DB-EEFieldPublic*/
insert into Dictionary values ("68","es","Importación de fichas","XX","La importación de fichas permite ingresar registros de desastres a DesInventar de forma masiva. Requiere un archivo en formato CSV y cada ficha requiere tener un número serial único, una fecha de ocurrencia, un código de la unidad geográfica, una fuente, un evento y una causa."," "); /*68-DB-Import*/
insert into Dictionary values ("100","es","Serial","No duplicado Max 40 caracteres","Número que identifica cada ficha. Se puede utilizar una sigla o un código, se recomienda asignar números secuenciales. Se pueden incluir letras, guiones y otros caracteres. El identificador o Número serial es único para cada Ficha.","Antes de iniciar el ingreso de información a su inventario diseñe una estrategia para la identificación de los seriales. Hay varias opciones dependiendo de sus necesidades. A continuación dos posibilidades: <br><br> a. Puede utilizar los primeros dígitos del serial para identificar el año en el que ocurrió el desastre y en los siguientes dígitos utilizar un número para identificar cada ficha, por ejemplo 1978-1200, correspondería al registro número 1200 del año 1978.<br>b. Si el inventario que va a construir es el de un sistema nacional que trabaja en prevención y atención de desastres, el cual opera por zonas, usted podría incluir en el serial un código para identificar los seriales provenientes de cada zona operativa. Por ejemplo la zona operativa del occidente del pais podría registrar una ficha como OCC-1200, o, OCC-1978-1200. Donde OCC es el código que identifica la zona occidente del país y 1200 correspondería al serial. <br><br>Si en su institución se guardan registros en papel (formatos) o en digital con datos de los desastres (fotos, reportes) usted puede anotar el Serial en dichos documentos para facilitar posteriores búsquedas de información.<br><br>Esta numeración es independiente de la codificación interna de las registros en la base de datos, por eso puede ser manipulada según necesidades del inventariador."); /*100-Disaster-Serial*/
insert into Dictionary values ("101","es","Fecha Inicio","Escriba una fecha en formato AAAA-MM-DD","En este campo se consignan el año, mes y día de ocurrencia del desastre.","Aunque se busca recopilar información para los tres campos: año, mes y día, esto no siempre es posible. En algunos casos puede no ser fácil determinar con exactitud el día (incluso el mes) de inicio de una inundación, de una epidemia o de una plaga. En dichos casos se recomienda que agote los recursos disponibles para conocer el mes; sino ingrese solamente el año."); /*101-Disaster-BeginTime*/
insert into Dictionary values ("102","es","Fuentes ","Texto con formato","Fuentes de información utilizadas para obtener los datos registrados en la Ficha. Deben incluirse una o varias fuentes según sea el caso. Éstas pueden ser: el o los medios de información, las bases de datos preexistentes y la entidad o las entidades de donde se obtuvo la información.","El registro de las fuentes de información debe ser riguroso tanto para inventarios históricos con base en archivos y fuentes institucionales, como para inventarios en tiempo cuasi real en desarrollo por instituciones nacionales o locales de gestión de riesgos. Es recomendable usar abreviaturas para cada fuente y anotar sus datos en orden decreciente respecto a la calidad y credibilidad de cada una. De esta manera es más fácil evaluar los inventarios y hacer preguntas y análisis posteriores en las bases de datos. Por las mismas razones es importante que los datos sean homogéneos, es decir, usar siempre la misma ortografía, las mismas abreviaturas, tipos de letras (mayúsculas o minúsculas) y signos."); /*102-Disaster-Source*/
insert into Dictionary values ("103","es","Geografía","Seleccione de las listas","Los campos de geografía responden a la resolución y los niveles geográficos del inventario. Determine y verifique la localización del desastre (en términos de los niveles establecidos) antes de iniciar la entrada de los datos.","Utilice la fuente de información en sí misma, pero consulte, hasta donde sea posible, la cartografía oficial para verificar que la localización corresponda a unidades geográficas correctas. Encontrará que muchos de los errores (p. ej. un municipio o condado que no pertenece a un cierto estado) pueden ser detectados por DesInventar. En otros casos, sobre todo a partir de fuentes hemerográficas, la resolución detallada no está disponible porque no hay seguridad sobre cuáles de estos niveles fueron afectados; por ello, los niveles subordinados de la geografía nó son estrictamente obligatorios. No obstante, se espera que siempre se registre el nivel geográfico que corresponde a la resolución de la base de datos (el de mayor detalle), ya que, de lo contrario, se asumirá que el desastre ocurrió en todos los elementos (unidades geográficas) del nivel superior, lo que por supuesto influye negativamente en la calidad de la base de datos."); /*103-Disaster-GeographyId*/
insert into Dictionary values ("104","es","Sitio","Texto descriptivo","Lugar específico donde ocurrió el desastre. Si el sitio corresponde con el último de los niveles geográficos reportados, no es necesario volver a digitarlo. El sitio puede ser una localidad, una vereda, un río, un territorio que cubre varias unidades geográficas, una finca o hacienda, un paraje, un lugar, una aldea, un accidente geográfico, etc., en el caso de desastres en areas rurales; y en el caso de desastres urbanos, la dirección y/o el barrio.","Si el sitio corresponde con el último de los niveles geográficos reportados, no es necesario volver a digitarlo. El -Sitio- puede ser una localidad, una vereda, un río, un territorio que cubre varias unidades geográficas, una finca o hacienda, un paraje, un lugar, una aldea, un accidente geográfico, etc., en el caso de desastres en areas rurales; y en el caso de desastres urbanos, la dirección y/o el barrio."); /*104-Disaster-SiteNotes*/
insert into Dictionary values ("105","es","Latitud","Grados decimales","Latitud del lugar específico donde ocurrió el desastre. Utilice como referencia el sistema WGS84. "," "); /*105-Disaster-Latitude*/
insert into Dictionary values ("106","es","Longitud","Grados decimales","Longitud del lugar específico donde ocurrió el desastre. Utilice como referencia el sistema WGS84. "," "); /*106-Disaster-Longitude*/
insert into Dictionary values ("107","es","Guardar como","Seleccione una opción","Indica la disponibilidad futura de la ficha, facilitando el proceso de control de calidad de la base de datos. Escoja una opción para guardar esta ficha."," "); /*107-Record|1-Status*/
insert into Dictionary values ("108","es","Autor"," ","Usuario que ha creado esta ficha."," "); /*108-Record|2-Author*/
insert into Dictionary values ("109","es","Fecha creación"," ","Fecha de creación de la ficha."," "); /*109-Record|2-Creation*/
insert into Dictionary values ("110","es","Fecha actualización"," ","Última fecha de actualización de la ficha."," "); /*110-Record|2-LastUpdate*/
insert into Dictionary values ("111","es","Tipo de evento","Seleccione de la lista","El evento es el fenómeno - natural, socio-natural o tecnológico - que actúa como el detonante de los efectos adversos sobre las vidas humanas, la salud y/o la infraestructura económica y social de una comunidad. Recuerde que en DesInventar evento es diferente a desastre. A diferencia de instituciones especializadas como servicios meteorológicos y sismológicos, en DesInventar no se incluyen todos los eventos sino sólo aquellos que tienen asociados efectos adversos (por ejemplo, no se incluyen lluvias sin efectos adversos, terremotos de bajas magnitudes e intensidades sin daños, inundaciones en regiones no ocupadas ni cultivadas sin daños, etc.).","DesInventar dispone de una lista predefinida de 36 tipos de eventos. La selección de vocablos para denominar los tipos de eventos no es “ortodoxa”, vista desde disciplinas como la geología, la meteorología, etc, sinó que pretende aproximarse a las designaciones más comunes para los diversos fenómenos en los paises. Para su selección se consultaron diversos diccionarios y glosarios técnicos, sin preteder ser exhaustivos y se predefinieron con ésto y con discusiones en talleres internacionales en los cuales participaron investigadores de las ciencias sociales, económicas y físico - naturales. Se trata de proveer un conjunto de términos, lo más homogéneos posibles, de utilidad para proyectos a partir de fuentes de información no siempre especializadas en los diferentes países."); /*111-Event-Id*/
insert into Dictionary values ("112","es","Duración","Cantidad en días","Número de días que duró el evento. La mayoría de los eventos pueden durar algunos minutos (i. e. sismos, deslizamientos súbitos) u horas (i. e. inundaciones o incendios). Eventos como sequías o inundaciones prolongadas pueden durar meses.","La mayoría de los eventos pueden durar algunos minutos (i. e. sismos, deslizamientos súbitos) u horas (i. e. inundaciones o incendios). Eventos como sequías o inundaciones prolongadas pueden durar meses."); /*112-Event-Duration*/
insert into Dictionary values ("113","es","Magnitud","Valor de magnitud","Magnitud del evento. En este campo se registran los valores de magnitud internacionalmente usados (i.e. escala de Richter para sismos, escala de Saffir Simpson para huracanes); variables cuantificadas (velocidad de los vientos, área del deslizamiento, centímetros cúbicos de tierra deslizada, valor de las precipitaciones, etc.) o descripciones breves tales como -leve-, -medio-, -alto-, -extremo-, etc.","Corresponde con valores de magnitud internacionalmente usadas para los eventos sismo, erupción volcánica, tsunami, huracán - tormenta tropical. Para otros tipos de evento el usuario podrá incluir variables cuantificadas tales como velocidad de vientos, alturas en metros de avenidas torrenciales e inundaciones, cantidad de lluvia en un sitio en 24 horas, área volumen de un deslizamiento o de un depósito. Se utiliza siempre las mismas unidades para cada tipo de variable: km/hora, m, mm/día, m2, m3, km3. Si algún equipo de trabajo tiene (o desarrolla) una metodología para medir las magnitudes de los desastres, se recomienda que cree un campo en la sección de  Efectos Adicionales."); /*113-Event-Magnitude*/
insert into Dictionary values ("114","es","Observaciones de evento","Texto o palabras claves","Descripción corta de la ocurrecia del evento. En el caso de eventos en cadena, eventos simultáneos incluye descripción de lo ocurrido."," "); /*114-Event-Notes*/
insert into Dictionary values ("115","es","Tipo de causa","Seleccione de la lista","Fenómeno inmediato que causó el evento. Normalmente las causas del desastre son una combinación de circunstancias propias del entorno sociocultural, económico, político e histórico, que se combinan con el evento detonante del desastre, sea éste un fenómeno de origen natural o antrópico.","El tema de la multicausalidad de los desastres es todavía un terreno de discusión académica y conceptual que no pretendemos agotar en DesInventar. Uno de los propósitos de la metodología DesInventar, y justamente uno de los motivos de su desarrollo, es validar o mejorar hipótesis sobre relaciones entre desastres y condiciones de vulnerabilidad. Para ello debe buscarse facilitar la relación entre los inventarios y otras variables sociales, económicas y medioambientales. En este sentido, se han predefinido un conjunto de causas cuya aplicación no siempre es evidente o directa. En algunos casos la causa se atribuye a fenómenos de mayor cobertura espacial y energía (sismos, huracanes, etc.), caso en el cual se -encubren- las demás variables que obviamente son fundamentales, como la planificación y usos del suelo, el hacinamiento y deterioro de las condiciones de vida de amplios segmentos de la población, etc.  Se recomienda evitar el uso de las causas -Otra causa- y -Desconocida-, antes de revisar exhaustivamente la lista de causas y, dependiendo de las necesidades y del contexto, crear la que haga falta."); /*115-Cause-Id*/
insert into Dictionary values ("116","es","Observaciones de causa","Texto o palabras claves","Anotación breve de las relaciones ente la causa y el evento. Es un campo muy importante para los análisis de la base de datos pues en este se pueden describir aquellas causas que complementan o contextualizan la registrada en el campo Causa.","En sentido estricto al evaluar cada desastre, por más natural que parezca, éste ocurre como un desajuste entre las complejas variables y dinámicas que construyeron un entorno social o de infraestructura – servicios y los factores tanto internos (condiciones de vulnerabilidad) como externos (el evento de origen natural o antrópico), que pusieron en evidencia el riesgo y que condujeron a que se materializara. Este campo se incluyó para registrar todas aquellas variables y relaciones de causalidad del evento, por ejemplo, diferencias en la determinación de la causa de un evento como si un deslizamiento ocurrió solamente por las lluvias o por el inapropiado manejo de aguas residuales."); /*116-Cause-Notes*/
insert into Dictionary values ("117","es","Muertos","Digite la cantidad o seleccione de la lista","Número de personas fallecidas por causas directas. Cuando se dispone de datos oficiales finales se incluye este valor con las observaciones correspondientes, por ejemplo cuando haya diferencias entre las cifras oficialmente aceptadas y las de otras fuentes. Las presunciones sobre muertos, no verificados oficialmente, se registran en el campo -Observaciones de efectos- consignando la fuente de información."," "); /*117-Effect|PeopleGood-PeopleDead*/
insert into Dictionary values ("118","es","Afectados","Digite la cantidad o seleccione de la lista","Número de personas que sufren efectos indirectos o secundarios asociados a un desastre. Corresponde al número de personas, diferentes a damnificados, que sufren el impacto de los efectos secundarios de los desastres por razones como deficiencias en la prestación de servicios públicos, en el comercio, o en el trabajo, o por aislamiento. Si la información aparece por familias, calcular el número de personas según indicadores disponibles."," "); /*118-Effect|PeopleGood-PeopleAffected*/
insert into Dictionary values ("119","es","Evacuados","Digite la cantidad o seleccione de la lista","Número de personas evacuadas temporalmente de sus viviendas, lugares de trabajo, colegios, hospitales, etc. Si la información aparece por familias, calcular el número de personas según indicadores disponibles."," "); /*119-Effect|PeopleGood-PeopleEvacuated*/
insert into Dictionary values ("120","es","Desaparecidos","Digite la cantidad o seleccione de la lista","Número de personas cuyo paradero a partir del desastre es desconocido. Incluye personas presumiblemente muertas sin evidencias físicas. Los datos de muertos y de desaparecidos son mutuamente excluyentes, por lo tanto no los mezcle."," "); /*120-Effect|PeopleGood-PeopleMissing*/
insert into Dictionary values ("121","es","Reubicados","Digite la cantidad o seleccione de la lista","Número de personas que han sido trasladadas desde sus sitios de vivienda a nuevos emplazamientos permanentes. Si la información aparece por familias, calcular el número de personas según indicadores disponibles."," "); /*121-Effect|PeopleGood-PeopleRelocated*/
insert into Dictionary values ("122","es","Damnificados","Digite la cantidad o seleccione de la lista","Número de personas que han sufrido grave daño directamente asociado al evento en sus bienes y/o servicios individuales o colectivos. Por ejemplo destrucción parcial o total de su vivienda y sus bienes; pérdidas en cultivos y/o en bodegas, etc. Se deben incluir también personas reubicadas. Si la información aparece por familias, se debe calcular el número de personas según indicadores disponibles."," "); /*122-Effect|PeopleGood-PeopleHarmed*/
insert into Dictionary values ("123","es","Heridos, enfermos","Digite la cantidad o seleccione de la lista","Número de personas que resultaron afectadas en su salud o integridad física, sin ser víctimas mortales, por causa directa del desastre. Se deben incluir las personas que sufrieron lesiones y las que quedaron enfermas si se trata de una plaga o epidemia."," "); /*123-Effect|PeopleGood-PeopleInjured*/
insert into Dictionary values ("124","es","Viv. afectadas","Digite la cantidad o seleccione de la lista","Número de viviendas con daños menores, no estructurales o arquitectónicos, que pueden seguir siendo habitadas, aún cuando requieran de acciones de reparación o de limpieza."," "); /*124-Effect|PeopleGood-HousesAffected*/
insert into Dictionary values ("125","es","Viv. destruidas","Digite la cantidad o seleccione de la lista","Número de viviendas arrasadas, sepultadas, colapsadas o deterioradas de tal manera que no son habitables."," "); /*125-Effect|PeopleGood-HousesDestroyed*/
insert into Dictionary values ("126","es","Valor pérdidas $","Valor en moneda local","Monto de las pérdidas directas causadas por el desastre en la moneda local."," "); /*126-Effect|Economic-LossesValueLocal*/
insert into Dictionary values ("127","es","Valor pérdidas US$","Valor en dólares de EEUU","Equivalente en dólares (US$) de las pérdidas en moneda local, según la tasa de cambio de moneda nacional en la fecha de ocurrencia del desastre. Este valor es útil para evaluaciones comparativas entre bases de datos."," "); /*127-Effect|Economic-LossesValueUSD*/
insert into Dictionary values ("128","es","Centros hospitalarios","Cantidad","Número de centros de salud, clínicas, hospitales locales y regionales destruidos y afectados directa o indirectamente por el desastre."," "); /*128-Effect|Affected|SectorHealth-MedicalCenters*/
insert into Dictionary values ("129","es","Centros de educación","Cantidad","Número de guarderías, escuelas, colegios, universidades, centros de capacitación, etc. destruidas y afectadas directa o indirectamente por el desastre. Incluye aquellos que han sido utilizados como albergues temporales. "," "); /*129-Effect|Affected|SectorEducation-EducationCenters*/
insert into Dictionary values ("130","es","Cultivos y bosques (Ha)","Cantidad","Número de áreas de cultivo, pastizales o bosques destruidas y afectadas. Si la información está en otra medida se deberá convertir a hectáreas. "," "); /*130-Effect|Affected|SectorAgricultural-FarmingAndForest*/
insert into Dictionary values ("131","es","Ganado","Cantidad","Número de unidades perdidas (bovinos, porcinos, caprinos, avícolas) cualquiera sea el evento (inundación, sequía, epidemia, etc.). "," "); /*131-Effect|Affected|SectorAgricultural-LiveStock*/
insert into Dictionary values ("132","es","Vías afectadas (m)","Cantidad","Longitud de redes viales destruidas y/o inhabilitadas en metros."," "); /*132-Effect|Affected|SectorTransport-Roads*/
insert into Dictionary values ("133","es","Otras pérdidas","Texto","Descripción de otras pérdidas no incluidas en los campos de la ficha básica. Por ejemplo: edificaciones religiosas y monumentos, patrimonio arquitectónico y cultural, escenarios e instalaciones masivas, edificios de administración pública de la banca, del comercio y del turismo, vehículos o buses perdidos, puentes.","Valor calculado o estimado de las pérdidas debidas a efectos indirectos o inducidos, atribuibles a interrupciones o deficiencias en la prestación de servicios públicos, en el comercio, en el trabajo, así como por aislamiento geográfico. En caso de no tener el monto de estas pérdidas se pueden anotar aquellos efectos que no se encuentran en los campos de la ficha. También es posible incluir el cálculo de lo que costaría o costó la reparación o reconstrucción de los daños provocados por el desastre."); /*133-Effect|More|SectorOther-OtherLosses*/
insert into Dictionary values ("134","es","Observaciones de efectos","Texto","Anotaciones o ampliaciones sobre los efectos del desastre.Se usa para registrar las discrepancias entres las cifras de efectos cuando hay mas de una fuente. Al igual que los campos de observaciones de eventos y causes se para contextualizar los datos consignados en la ficha ess en esta caso para los efectos.","De acuerdo con la secuencia de campos de la Ficha Básica, se recomienda utilizar las siguientes claves para las observaciones (se debe digitar la clave y a continuación la observación correspondiente): R. Reubicados, T. Transporte, Vd. Viviendas destruidas, Ac. Acueducto, Va. Viviendas afectadas, En. Energía, V. Vías, I. Industria, A. Agricultura, Al. Alcantarillado, P. Pecuario, C. Comunicaciones, S. Salud, Ma. Magnitud, Ed. Educación, Fe. Fecha de fín de un evento de largo período, Vp. Valor pérdidas."); /*134-Effect|More-Notes*/
insert into Dictionary values ("135","es","Transporte","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Corresponde con efectos del desastre sobre el sector del transporte: redes viales (vehiculares, férreas), terminales de transporte, aeropuertos, puentes fluviales y marítimos, muelles, etc. afectados y/o destruidos."," "); /*135-Sector-Transport*/
insert into Dictionary values ("136","es","Comunicaciones","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Se relaciona con los daños en el sector de comunicaciones: sobre plantas y redes telefónicas, estaciones de radio y televisión, oficinas de correo y de información pública, servicios de internet, radiotelefonía, comunicaciones celulares."," "); /*136-Sector-Communications*/
insert into Dictionary values ("137","es","Instalaciones de socorro","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Se relaciona con los daños en el sector de respuesta a emergencia, específicamente instalaciones de los organismos de socorro: Cuerpos de bomberos, instalaciones de organismos de socorro y de entidades de orden público. "," "); /*137-Sector-Relief*/
insert into Dictionary values ("138","es","Agropecuario","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Se relaciona con los daños en el sector de Agropecuario: Campos de cultivos, granjas, zonas de pastoreo."," "); /*138-Sector-Agricultural*/
insert into Dictionary values ("139","es","Acueducto","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Se relaciona con los daños en el sector de Acueducto: Tomas de agua, plantas de tratamiento, acueductos y canales de conducción de agua potable, tanques de almacenamiento."," "); /*139-Sector-WaterSupply*/
insert into Dictionary values ("140","es","Alcantarillado","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Se relaciona con los daños en el sector de Alcantarillado: Redes de disposición de aguas servidas y/o pluviales y sus plantas de tratamiento."," "); /*140-Sector-Sewerage*/
insert into Dictionary values ("141","es","Educación","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Se relaciona con los daños en el sector de Educación: Todo lo relacionado con este sector: guarderías, escuelas, colegios, universidades, centros de capacitación, bibliotecas, centros culturales, etc."," "); /*141-Sector-Education*/
insert into Dictionary values ("142","es","Energía","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Se relaciona con los daños en el sector energético: Presas, subestaciones, líneas de transmisión, plantas de generación, plantas de procesamiento de energéticos y depósitos combustibles, oleoductos, gasoductos, plantas nucleares."," "); /*142-Sector-Power*/
insert into Dictionary values ("143","es","Industrias","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Se relaciona con los daños en el sector de industrial:Industrias de todos los tipos y tamaños, incluidas agrícolas y pecuarias."," "); /*143-Sector-Industry*/
insert into Dictionary values ("144","es","Salud","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Se relaciona con los daños en el sector de la salud: Todo lo relacionado con el sector de salud incluyendo las redes de comunicación, red de atención de emergencias (ambulancias), centros de atención, etc."," "); /*144-Sector-Health*/
insert into Dictionary values ("145","es","Otros","Elija una opción","Campo cualitativo de la base de datos. Tiene dos opciones “Afectado o Nó Afectado”. Se relaciona con daños que no corresponde  a ninguno de los campos de la ficha básica."," "); /*145-Sector-Other*/
insert into Dictionary values ("146","es","Borrador","Elija una opción","Corresponde a uno de los estados de almacenamiento de la ficha: Ficha en edición, aún no terminada (responsabilidad del Alimentador)"," "); /*146-RecordStatus-Draft*/
insert into Dictionary values ("147","es","Por aprobar","Elija una opción","Corresponde a uno de los estados de almacenamiento de la ficha: Ficha que se encuentra lista para ser aprobada (por el supervisor)."," "); /*147-RecordStatus-Aprobe*/
insert into Dictionary values ("148","es","Eliminar","Elija una opción","Corresponde a uno de los estados de almacenamiento de la ficha: Ficha que se encuentra lista para ser eliminada (por el supervisor)"," "); /*148-RecordStatus-Trash*/
insert into Dictionary values ("149","es","Publicada","Elija una opción","Corresponde a uno de los estados de almacenamiento de la ficha: Ficha de consulta pública. Ya ha sido aprobada para su publicación por parte del Supervisor."," "); /*149-RecordStatus-Publish*/
insert into Dictionary values ("150","es","Eliminada!","Elija una opción","Corresponde a uno de los estados de almacenamiento de la ficha: Ficha eliminada por el Supervisor"," "); /*150-RecordStatus-Delete*/
