insert into Dictionary values ("207","es","Tipo de Gráfico","Seleccione un tipo gráfico","Los tipos de gráficos disponibles son: histogramas temporales, estacionales y gráficos comparativos."," "); /*207-Graph-Type*/
insert into Dictionary values ("208","es","Temporal","Histograma de barras o lineas, el eje X corresponde al tiempo, el Y al efecto a graficar","Histograma temporal. Representación gráfica de una variable de los efectos en forma de barras o líneas, donde la superficie de cada barra/línea corresponde a la frecuencia del valor representado (eje vertical), según el periodo que el usuario seleccione, el cual se despliega en el eje horizontal."," "); /*208-Graph-HisTemporal*/
insert into Dictionary values ("209","es","Tipo de evento/temporal","Igual que el histograma temporal discrimina por eventos en el eje de las X.","Histograma evento/temporal. Representación gráfica de uno o mas eventos en términos de una variable de efectos en forma de barras o líneas, donde la altura alcanzada por la barra/línea corresponde con la frecuencia del valor representado (eje vertical), según el periodo de tiempo (eje horizontal) que el usuario seleccione."," "); /*209-Graph-HisEveTemporal*/
insert into Dictionary values ("210","es","/temporal","Igual que el histograma temporal discrimina por unidades geográficas en el eje de las X.","Histograma Geográfico/temporal: Representación gráfica de uno a seis unidades geográficas en términos de una variable de efectos en forma de barras o líneas, donde la altura alcanzada por la barra/línea corresponde con la frecuencia del valor representado (eje vertical), según el periodo de tiempo (eje horizontal) que el usuario seleccione."," "); /*210-Graph-HisGeoTemporal*/
insert into Dictionary values ("211","es","Tipo de causa/temporal","Discrimina por causas en el Eje X","Representación gráfica de una a seis causas en términos de una variable de efectos en forma de barras o líneas, donde la altura alcanzada por la barra/línea corresponde con la frecuencia del valor representado (eje vertical), según el periodo de tiempo (eje horizontal) que el usuario seleccione."," "); /*211-Graph-HisCauTemporal*/
insert into Dictionary values ("212","es","Por tipo de eventos","Compara los eventos en un gráfico tipo torta, según la variable a graficar","Comparativo de eventos: Gráfico comparativo -tipo torta- de una variable (de efectos o de número de reportes) agrupados por los eventos seleccionados"," "); /*212-Graph-ComByEvents*/
insert into Dictionary values ("213","es","Por ","Compara las unidades geográficas en un gráfico tipo torta, según la variable a graficar","Comparativo de unidades geográficas: Gráfico comparativo -tipo torta- de una variable (de efectos o de número de reportes) agrupados por las unidades geográficas seleccionados"," "); /*213-Graph-ComByGeography*/
insert into Dictionary values ("214","es","Por tipo de causas","Compara las causas en un gráfico tipo torta, según la variable a graficar","Comparativo de causas: Gráfico comparativo -tipo torta- de una variable (de efectos o de número de reportes) agrupados por las causas seleccionados"," "); /*214-Graph-ComByCauses*/
