insert into Dictionary values ("207","fr","Type de graphique","Select a graphic type","The types of graphics available are: temporal histograms, seasonal and comparative graphs."," "); /*207-Graph-Type*/
insert into Dictionary values ("208","fr","Temporal","Bar or line histogram. The x axis corresponds to time and the Y axis to the effect","Temporar histogram. Graphic representation of one of the variables of effects in line or bar format, where the top of every bar/line corresponds to the frequency of the value represented (vertical axis), according to the period defined by the user, which is displayed on the horizontal axis."," "); /*208-Graph-HisTemporal*/
insert into Dictionary values ("209","fr","Compétition / temporelle","The same as the temporal histogram, it discriminates between events on the x axis.","Event/Temporal histogram. Graphic representation of one or more events in terms of the variables of effects in line or bar format, where the height of the line/bar corresponds to the frequency of the value represented (vertical axis), according to the time period defined by the user (horizontal axis)."," "); /*209-Graph-HisEveTemporal*/
insert into Dictionary values ("210","fr","/ temporelles","The same as the temporal histogram, it discriminates between geographical units on the x axis.","Geographical/Temporal histogram.  Graphic representation of one to six geographical units in terms of the variables of effects in line or bar format, where the height of the line/bar corresponds to the frequency of the value represented (vertical axis), according to the time period defined by the user (horizontal axis)."," "); /*210-Graph-HisGeoTemporal*/
insert into Dictionary values ("211","fr","Cause / temporelle","Discriminates causes on the X axis.","Graphic representation of one to six causes in terms of the variables of effects in line or bar format, where the height of the line/bar corresponds to the frequency of the value represented (vertical axis), according to the time period defined by the user (horizontal axis)."," "); /*211-Graph-HisCauTemporal*/
insert into Dictionary values ("212","fr","Par les événements","Compares events in a pie chart, according to the variable.","Comparison of events: Comparative pie chart – of a variable (of effects or number of reports) grouped by selected events."," "); /*212-Graph-ComByEvents*/
insert into Dictionary values ("213","fr","Par ","Compares geographical units in a pie chart, according to the variable","Comparison of geographical units. Comparative pie chart – of a variable (of effects or number of reports) grouped by selected geographical units."," "); /*213-Graph-ComByGeography*/
insert into Dictionary values ("214","fr","Par causes","Compares causes in a pie chart, according to the variable","Comparison of causes: Comparative pie chart – of a variable (of effects or number of reports) grouped by selected causes."," "); /*214-Graph-ComByCauses*/
