insert into Dictionary values ("35","pt","Possui mapa?","Marque ou desmarque esta opção","Caso haja um mapa para este nível, selecione esta opção para associá-lo ao Banco de Dados."," "); /*35-en-Map?*/
insert into Dictionary values ("36","pt","Nome do mapa","Insira um nome","Nome do plano de informação ou Layer do mapa do banco de dados que contém os elementos geográficos. Em formato Shape é o mesmo nome do arquivo (sem a extensão)."," "); /*36-en-Map Name*/
insert into Dictionary values ("37","pt","Coluna Código","Insira um valor","Cabeçalho da coluna que contém o código dos elementos geográficos"," "); /*37-en-Column Code*/
insert into Dictionary values ("38","pt","Coluna Nome","Insira um valor","Cabeçalho da coluna que contém o código dos elementos geográficos"," "); /*38-en-Column Name*/
insert into Dictionary values ("39","pt","Elementos","Insira ou edite um elemento geográfico","Listas de unidades geográficas que compõem cada nível."," "); /*39-en-Elements*/
insert into Dictionary values ("40","pt","Código","Insira um valor","Código oficial ou comumente utilizado na região. Deve corresponder com a cartografia."," "); /*40-en-Code*/
insert into Dictionary values ("41","pt","Nome","Insira um texto","Nome oficial ou comumente utilizado na região. Este nome deve corresponder com o designado na cartografia."," "); /*41-en-Name*/
insert into Dictionary values ("42","pt","Nível superior","Selecione um elemento da lista, caso se aplique","Elemento de nível superior ao qual pertence o atual elemento geográfico."," "); /*42-en-Parent*/
insert into Dictionary values ("43","pt","Ativar","Marque ou desmarque esta opção","Indica se o presente elemento estará disponível ou não para visualização nas listas. Isto pode ocorrer porque o elemento geográfico se desagregou e atualmente não existe como tal."," "); /*43-en-Activate*/
insert into Dictionary values ("44","pt","Administração de eventos"," ","Gestão dos campos de eventos pré-definidos por DesInventar e personalizados no banco de dados."," "); /*44-en-Administration of events*/
insert into Dictionary values ("45","pt","Evento pré-definido","Insira um nome","Nome do evento pré-definido. Somente o administrador do banco de dados poderá editar este nome de modo a assemelhar-se ao termo local conhecido, com a condição de que seja fiel à definição."," "); /*45-en-Predefined event*/
insert into Dictionary values ("46","pt","Evento personalizado","Insira um nome","Nome do evento pré-definido. Somente o administrador do banco de dados poderá criar estes eventos. Estes somente poderão ser consultados no banco de dados onde foram criados."," "); /*46-en-Personalised event*/
insert into Dictionary values ("47","pt","Definição","Texto ou palavras-chave","Texto descritivo do evento oficial de DesInventar para o banco de dados. A diferença do nome, a descrição não poderá ser alterada pelo Administrador do banco de dados, a fim de garantir a comparação entre os bancos de dados."," "); /*47-en-Definition*/
insert into Dictionary values ("48","pt","Definição","Texto ou palavras-chave","Texto descritivo do evento."," "); /*48-en-Definition*/
insert into Dictionary values ("49","pt","Ativar","Marque ou desmarque esta opção","Indica se o evento estará disponível ou não para visualização no formulário básico. Isto será útil para os casos onde o evento não tem aplicabilidade no banco de dados."," "); /*49-en-Active*/
insert into Dictionary values ("50","pt","Administração de causas"," ","Gestão dos campos de causas pré-definidas por DesInventar e personalizadas no banco de dados"," "); /*50-en-Administration of Causes*/
insert into Dictionary values ("51","pt","Causa pré-definida","Insira um nome","Nome da causa pré-definida. Somente o administrador do banco de dados poderá editar o nome da causa de modo a assemelhar-se ao termo local conhecido, com a condição de que coincida com a definição."," "); /*51-en-Predefined Cause*/
insert into Dictionary values ("52","pt","Causa personalizada","Insira um nome","Nome da causa personalizada. Somente o administrador do banco de dados poderá criar uma causa. Estas causas somente poderão ser consultadas no banco de dados onde foram criadas."," "); /*52-en-Personalised cause*/
insert into Dictionary values ("53","pt","Definição","Texto ou palavras-chave","Texto descritivo da causa pré-definida. A diferença do nome, a definição não poderá ser alterada, a fim de garantir a comparação entre os bancos de dados."," "); /*53-en-Definition*/
insert into Dictionary values ("54","pt","Definição","Texto ou palavras-chave","Texto descritivo da causa."," "); /*54-en-Definition*/
insert into Dictionary values ("55","pt","Ativar","Marque ou desmarque esta opção","Indica se a causa estará ou não disponível para visualização no formulário básico. Isto pode ser útil nos casos onde a causa não tenha aplicabilidade para o banco de dados."," "); /*55-en-Active*/
insert into Dictionary values ("56","pt","Administração de efeitos adicionais"," ","Gestão dos campos de Efeitos Adicionais e Grupos de Efeitos"," "); /*56-en-Administration of Additional Effects*/
insert into Dictionary values ("57","pt","Rótulo","Insira um nome","Nome visível do efeito adicional"," "); /*57-en-Label*/
insert into Dictionary values ("58","pt","Definição","Texto ou palavras-chave","Descrição do campo do efeito adicional"," "); /*58-en-Definition*/
insert into Dictionary values ("59","pt","Tipo","Selecione da lista","Tipo de dado a inserir "," "); /*59-en-Type*/
insert into Dictionary values ("60","pt","Tamanho","Valor numérico","Largura máxima do campo"," "); /*60-en-Size*/
insert into Dictionary values ("61","pt","Ordem","Valor numérico","Ordem de visualização"," "); /*61-en-Order*/
insert into Dictionary values ("62","pt","Ativar","Marque ou desmarque esta opção","Indica se o campo encontra-se ativo. Caso seja desativado, os usuários não poderão visualizá-lo nos módulos DesInventar."," "); /*62-en-Active*/
insert into Dictionary values ("63","pt","Público","Marque ou desmarque esta opção","Indica se o campo será de consulta pública pelo módulo DesConsultar."," "); /*63-en-Public*/
insert into Dictionary values ("68","pt","Importação de formulários"," ","A importação de formulários permite ingressar registros de desastres em DesInventar de forma massiva. Requere-se um arquivo CSV e cada formulário deve ter um número serial único, uma data de ocorrência, um código da unidade geográfica, uma fonte, um evento e uma causa."," "); /*68-en-Datacard importation*/
insert into Dictionary values ("100","pt","Serial","Não duplicado Max 40 caracteres","Número que identifica cada formulário. Pode-se utilizar uma sigla ou um código. Recomenda-se designar números seqüenciais. Podem-se incluir letras, roteiros ou outros caracteres. O identificador ou número serial é único para cada formulário.","Antes de iniciar o ingresso de informações a seu inventário elabore uma estratégia para a identificação dos seriais. Existem várias opções dependendo de suas necessidades. A continuação duas possibilidades: <br><br> a. Pode utilizar os primeiros dígitos do serial para identificar o ano em que ocorreu o desastre e nos dígitos seguintes  utilizar um número para identificar cada formulário, por exemplo 1978-1200 corresponde ao registro número 1200 do ano de 1978.<br>b. Se o inventário que você irá construir é de um sistema nacional que trabalha na prevenção e atendimento de desastres, o qual opera por zonas, você poderá incluir no serial um código para identificar os seriais provenientes de cada zona operacional. Por exemplo, a zona operacional de um Estado do País poderia registrar um formulário como RS-1200, ou, RS-1978-1200, onde RS é o código que identifica o Estado do Rio Grande do Sul e 1200 corresponde ao serial. <br><br>Se na sua instituição se arquivam registros em papel ou em formato digital com dados dos desastres (fotos, reportagens) você pode anotar o Serial nos documentos para facilitar posteriores pesquisas.<br><br>Esta numeração é independente da codificação interna dos registros no banco de dados, por isso pode ser manipulada de acordo com as necessidades do usuário."); /*100-en-Serial*/
insert into Dictionary values ("101","pt","Data inicial","Escreva uma data no formato AAAA-MM-DD","Neste campo se insere o ano, mês e dia da ocorrência do desastre.","Embora se pretenda recolher informações para os três campos: ano, mês e dia, isto nem sempre é possível. Em alguns casos pode não ser fácil determinar com exatidão o dia (inclusive o mês) do início de uma inundação, de uma epidemia ou de uma praga. Nestes casos recomenda-se que se esgotem os recursos disponíveis para conhecer o mês; caso contrário insira apenas o ano."); /*101-en-Start date*/
insert into Dictionary values ("102","pt","Fontes","Texto com formato","Fontes de informação utilizadas para obter os dados registrados no formulário. Devem incluir uma ou várias fontes conforme o caso. Estas podem ser: meios de comunicação, bancos de dados pré-existentes e a entidade(s) de onde se obteve a informação","O registro das fontes de informação deve ser rigoroso tanto para inventários históricos com base em arquivos e fontes institucionais, quanto para inventários em tempo quase real em desenvolvimento por instituições nacionais ou locais de gestão de riscos. Recomenda-se o uso de abreviaturas para cada fonte e anotar seus dados em ordem decrescente em relação à qualidade e credibilidade de cada uma. Assim torna-se mais fácil avaliar os inventários e fazer perguntas e análises posteriores nos bancos de dados. Pelas mesmas razões é importante que os dados sejam homogêneos, ou seja, deve-se usar sempre a mesma ortografia, as mesmas abreviaturas, os mesmos tipos de letra (maiúsculas ou minúsculas) e sinais de pontuação."); /*102-en-Sources*/
insert into Dictionary values ("103","pt","Geografia","Selecione das listas","Os campos de geografia respondem à resolução e os níveis geográficos do inventário. Determine e verifique a localização do desastre (em termos dos níveis estabelecidos) antes de iniciar a entrada dos dados.","Utilize a própria fonte de informação, mas consulte, até onde seja possível, a cartografia oficial para verificar que a localização corresponda a unidades geográficas corretas. DesInventar pode detectar muitos dos erros cometidos pelo usuário (ex. um município que não pertence a um Estado). Em outros casos, principalmente a partir de fontes jornalísticas, a resolução detalhada não está disponível porque não há segurança sobre quais destes níveis foram afetados; conseqüentemente os níveis subordinados da geografia não são estritamente obrigatórios. No entanto, espera-se que seja sempre registrado o nível geográfico correspondente à resolução do banco de dados (o de maior detalhe), caso contrário, assume-se que o desastre ocorreu em todos os elementos (unidades geográficas) do nível superior, o que influi negativamente na qualidade do banco de dados."); /*103-en-Geography*/
insert into Dictionary values ("104","pt","Local","Texto descritivo","Lugar específico onde ocorreu um desastre. No caso de desastres em áreas rurais, este pode ser uma localidade, um povoado, um rio, um território que cobre várias unidades geográficas, um sítio ou fazenda, um parador, uma aldeia, um acidente geográfico, etc. Em desastres dentro de áreas urbanas o local corresponde ao endereço e/ou bairro. Se o local corresponde ao último dos níveis geográficos inseridos não é necessário digitá-lo novamente. ","Se o local corresponde ao último dos níveis geográficos inseridos não é necessário digitá-lo novamente. No caso de desastres em áreas rurais, este pode ser uma localidade, um povoado, um rio, um território que cobre várias unidades geográficas, um sítio ou fazenda, um parador, uma aldeia, um acidente geográfico, etc. Em desastres dentro de áreas urbanas o local corresponde ao endereço e/ou bairro. "); /*104-en-Place*/
insert into Dictionary values ("105","pt","Latitude","Graus decimais","Latitude do lugar específico onde ocorreu o desastre. Utilize como referência o sistema WGS84."," "); /*105-en-Latitude*/
insert into Dictionary values ("106","pt","Longitude","Graus decimais","Longitude do lugar específico onde ocorreu o desastre. Utilize como referência o sistema WGS84."," "); /*106-en-Longitude*/
insert into Dictionary values ("107","pt","Salvar como","Selecione uma opção","Indica a disponibilidade futura do formulário, facilitando o processo de controle de qualidade do banco de dados. Escolha uma opção para salvar este formulário."," "); /*107-en-Save As*/
insert into Dictionary values ("108","pt","Autor"," ","Usuário que criou este formulário"," "); /*108-en-Author*/
insert into Dictionary values ("109","pt","Data de criação"," ","Data de criação do formulário."," "); /*109-en-Creation date*/
insert into Dictionary values ("110","pt","Data de atualização"," ","Última data de atualização do formulário."," "); /*110-en-Updated*/
insert into Dictionary values ("111","pt","Evento","Selecione da lista","O evento é o fenômeno - natural, sócio-natural ou tecnológico - que atua como agente detonante dos efeitos adversos sobre as vidas humanas, a saúde e/ou a infra-estrutura econômica e social de uma comunidade. Lembre-se que em DesInventar evento é diferente de desastre. Diferentemente de instituições especializadas como serviços meteorológicos e sismológicos, em DesInventar não se incluem todos os eventos, mas somente aqueles que têm associados efeitos adversos (por exemplo, não se incluem chuvas sem efeitos adversos, terremotos de baixas magnitudes e intensidades sem danos, inundações em regiões não ocupadas nem cultivadas, etc.).","DesInventar dispõe de uma lista predefinida de 36 tipos de eventos. A seleção do vocabulário para nomear os tipos de eventos não é “ortodoxa”, na perspectiva das disciplinas como a geologia, a meteorologia, etc. Os termos pretendem se aproximar às designações mais comuns nos países para os diversos fenômenos. Para sua seleção foi feita uma consulta não exaustiva de dicionários e glossários técnicos juntamente com discussões em oficinas internacionais, das quais participaram pesquisadores das ciências sociais, econômicas e físico-naturais. Trata-se de prover um conjunto de termos, o mais homogêneo possível, de utilidade para projetos onde as fontes de informação nem sempre são especializadas."); /*111-en-Type of event*/
insert into Dictionary values ("112","pt","Duração","Quantidade de dias","Número de dias de duração do evento. A maioria dos eventos pode durar alguns minutos (por exemplo, sismos ou escorregamentos súbitos) ou horas (exemplo inundações ou incêndios). Eventos como secas ou inundações prolongadas podem durar meses."," "); /*112-en-Duration*/
insert into Dictionary values ("113","pt","Magnitude","Valor de magnitude","Magnitude do evento. Neste campo registram-se os valores de magnitude usados internacionalmente (por exemplo, a escala Richter para sismos ou a escala Saffir Simpson para furacões); variáveis quantitativas (velocidade dos ventos, área do escorregamento, metros cúbicos de terra escorregada, valor das precipitações, etc.) ou adjetivos qualitativos tais como leve, médio, alto ou extremo.","Correspondente aos valores de magnitude internacionalmente usados para eventos sísmicos, erupção vulcânica, tsunami e furacão - tempestade tropical. Para outros tipos de evento o usuário poderá incluir variáveis quantitativas, tais como velocidade dos ventos; nível de enxurradas e inundações; quantidade de chuva em 24 horas num local; área e/ou volume de um escorregamento. Sempre são utilizadas as mesmas unidades para cada tipo de variável: km/h, m, mm/dia, m2, m3, km3. Se alguma equipe de trabalho tem (ou desenvolve) uma metodologia para medir as magnitudes dos desastres, recomenda-se que se crie um campo na seção de Efeitos Adicionais."); /*113-en-Magnitude*/
insert into Dictionary values ("114","pt","Observações de evento","Texto ou palavras-chave","Descrição sucinta da ocorrência do evento. Nos casos de eventos em cadeia ou eventos simultâneos, incluir a descrição do ocorrido."," "); /*114-en-Observation about the event*/
insert into Dictionary values ("115","pt","Causa","Selecione da lista","Fenômeno imediato que causou o evento. Normalmente as causas do desastre são uma combinação de circunstâncias próprias do entorno socio-cultural, econômico, político e histôrico, que se combinam com o evento detonante do desastre, seja de origem natural ou antrópica.","A multi-causalidade dos desastres é um tema ainda em discussão acadêmica e epistemológica que não se pretende esgotar em DesInventar. Um dos propósitos da metodologia DesInventar, e justamente uma das motivações do seu desenvolvimento, é validar ou melhorar hipóteses sobre relações entre desastres e condições de vulnerabilidade. Para isto deve-se facilitar a relação entre os inventários e outras variáveis sociais, econômicas e meio-ambientais. Neste sentido, têm-se predefinido um conjunto de causas cuja aplicação nem sempre é evidente ou direta. Em alguns casos a causa se atribui a fenômenos de maior cobertura espacial e energia (sismos, furações, etc.), caso no qual se -encobrem- as demais variáveis que obviamente são fundamentais, tais como a planificação e o uso do solo; a superlotação e a deterioração das condições de vida de amplos segmentos da população, etc. Recomenda-se evitar o uso das causas -Outra causa- e -Desconhecida-, antes de revisar exaustivamente a lista de causas e, dependendo das necessidades e do contexto, criar a que estiver faltando."); /*115-en-Type of cause*/
insert into Dictionary values ("116","pt","Observações de causa","Texto ou palavras-chave","Anotação breve das relações entre a causa e o evento. É um campo muito importante para as análises do banco de dados porque neste pode-se descrever as causas que complementam ou contextualizam a registrada no campo Causa","Em sentido estrito ao avaliar cada desastre, por mais natural que aparente ser, este ocorre como conseqüência do desajuste entre as complexas variáveis e dinâmicas que construíram um entorno social (infra-estrutura-serviços) e os fatores tanto internos (condições de vulnerabilidade) quanto externos (o evento de origem natural ou antrópica), que evidenciaram o risco e conduziram a que este se materializará. Este campo foi incluído para registrar todas as variáveis e relações de causalidade do evento; por exemplo, no caso de um escorregamento, este pode ter sido causado somente por chuvas, mas também pelo manejo inadequado da rede de esgoto."); /*116-en-Observations about the cause*/
insert into Dictionary values ("117","pt","Mortos","Digite a quantidade ou selecione da lista","Número de pessoas falecidas por causas diretas. Quando se dispõe de dados oficiais finais este valor se inclui com as observações correspondentes; por exemplo, quando houver diferenças entre os números oficialmente aceitos e de outras fontes. As estimativas de mortos, não verificadas oficialmente, se registram no campo -Observações de Efeitos- ingressando a fonte de informação."," "); /*117-en-Deaths*/
insert into Dictionary values ("118","pt","Afetados","Digite a quantidade ou selecione da lista","Número de pessoas que sofreram os efeitos indiretos ou secundários associados a um desastre. Corresponde ao número de pessoas, diferentes das atingidas, que sofreram o impacto dos efeitos secundários dos desastres por razões como deficiências na prestação de serviços públicos, no comércio, no trabalho ou por isolamento. Se a informação aparecer por famílias, calcular o número de pessoas segundo indicadores disponíveis."," "); /*118-en-Affected*/
insert into Dictionary values ("119","pt","Evacuados","Digite a quantidade ou selecione da lista","Número de pessoas evacuadas temporariamente de suas residências, locais de trabalho, colégios, hospitais, etc. Se a informação aparecer por famílias, calcular o número de pessoas segundo indicadores disponíveis."," "); /*119-en-Evacuees*/
insert into Dictionary values ("120","pt","Desaparecidos","Digite a quantidade ou selecione da lista","Número de pessoas cujo paradeiro é desconhecido desde o momento do desastre. Inclui pessoas supostamente mortas, mas das quais não se têm evidência física. Os dados de mortos e desaparecidos são mutuamente excludentes, portanto não se deve misturar."," "); /*120-en-Missing*/
insert into Dictionary values ("121","pt","Desabrigados","Digite a quantidade ou selecione da lista","Número de pessoas que têm sido deslocadas desde suas moradias até novos locais permanentes. Se a informação aparecer por famílias, calcular o número de pessoas segundo indicadores disponíveis."," "); /*121-en-Relocated*/
insert into Dictionary values ("122","pt","Atingidos","Digite a quantidade ou selecione da lista","Número de pessoas que sofreram danos graves diretamente associados ao evento em seus bens e/ou serviços individuais ou coletivos. Por exemplo, a destruição parcial ou total da sua residência e bens ou as perdas em cultivos e/ou depósitos de colheita. Devem-se incluir também pessoas desabrigadas. Se a informação aparecer por famílias, deve-se calcular o número de pessoas segundo indicadores disponíveis."," "); /*122-en-Victims*/
insert into Dictionary values ("123","pt","Feridos, doentes","Digite a quantidade ou selecione da lista","Número de pessoas que foram afetadas na sua saúde ou integridade física, desconsiderando as mortes, por causa direta do desastre. Devem-se incluir as pessoas que sofreram lesões e as que ficaram doentes no caso de uma plaga ou epidemia."," "); /*123-en-Wounded, sick*/
insert into Dictionary values ("124","pt","Residências afetadas","Digite a quantidade ou selecione da lista","Número de residências com danos menores, não estruturais ou arquitetônicos, que podem seguir sendo habitadas, embora requeiram ações de reparação ou de limpeza."," "); /*124-en-Homes affected*/
insert into Dictionary values ("125","pt","Residências atingidas","Digite a quantidade ou selecione da lista","Número de residências arrasadas, soterradas, colapsadas ou deterioradas de tal maneira que não sejam mais habitáveis."," "); /*125-en-Homes destroyed*/
insert into Dictionary values ("126","pt","Valor perdas $","Valor em moeda local","Montante em moeda local das perdas diretas causadas pelo desastre."," "); /*126-en-Loss value $*/
insert into Dictionary values ("127","pt","Valor perdas US$","Valor em dólares de EEUU","Equivalente em dólares (US$) das perdas em moeda local, segundo a taxa de câmbio de moeda nacional na data de ocorrência do desastre. Este valor é útil para avaliações comparativas entre bancos de dados."," "); /*127-en-Loss value US$*/
insert into Dictionary values ("128","pt","Centros hospitalares","Quantidade","Número de centros de saúde, clínicas, hospitais locais e regionais destruídos e/ou afetados direta ou indiretamente pelo desastre."," "); /*128-en-Health Sector*/
insert into Dictionary values ("129","pt","Centros educativos","Quantidade","Número de creches, escolas, colégios, universidades, centros de capacitação, etc., destruídos e afetados direta ou indiretamente pelo desastre. Inclui aqueles que têm sido utilizados como albergues temporários. "," "); /*129-en-Educational centres*/
insert into Dictionary values ("130","pt","Cultivos e florestas (Ha)","Quantidade","Área de cultivo, pastagens ou floresta destruídos e afetados. Se a informação está em outra unidade deve-se converter para hectares. "," "); /*130-en-Crops and woods (Hectares)*/
insert into Dictionary values ("131","pt","Gado","Quantidade","Número de unidades perdidas (bovinos, suínos, caprinos, aves) qualquer que seja o evento (inundação, seca, epidemia, etc.). "," "); /*131-en-Livestock*/
insert into Dictionary values ("132","pt","Estradas afetadas (m)","Quantidade","Comprimento, em metros, das redes viárias destruídas e/ou desabilitadas"," "); /*132-en-Routes affected*/
insert into Dictionary values ("133","pt","Outras perdas","Texto","Descrição das perdas não incluídas nos campos do formulário básico. Por exemplo: edificações religiosas e monumentos, patrimônio arquitetônico e cultural, teatros e instalações massivas, edifícios de administração pública, do comércio e do turismo, veículos ou ônibus perdidos e pontes.","Valor calculado ou estimado das perdas devidas a efeitos indiretos ou induzidos, atribuíveis a interrupções ou deficiências na prestação de serviços públicos, no comércio, no trabalho, assim como por isolamento geográfico. No caso de não se dispor do montante destas perdas, podem-se anotar aqueles efeitos que não se encontram nos campos do formulário. Também é possível incluir o cálculo do que custaria ou custou a reparação ou a reconstrução dos danos provocados pelo desastre."); /*133-en-Other losses*/
insert into Dictionary values ("134","pt","Observações de efeitos","Texto","Anotações ou ampliações sobre os efeitos do desastre. Usa-se para registrar as discrepâncias entre as cifras de efeitos quando há mais de uma fonte. Igual aos campos de observações de eventos e causas usa-se para contextualizar os dados inseridos no formulário neste caso para os efeitos.","Conforme à seqüência de campos do Formulário Básico, recomenda-se utilizar as seguintes chaves para as observações: (deve-se digitar a chave e em seguida a observação correspondente): R. Desabrigados; T. Transporte; Vd. Residências destruídas; Ac. Aqueduto; Va. Residências afetadas; En. Energia; V. Estradas; I. Indústria; A. Agricultura; Al. Esgoto; P. Pecuária; C. Comunicações; S. Saúde; Ma. Magnitude; Ed. Educação; Fe. Data do fim de um evento de período comprido; Vp. Valor das perdas."); /*134-en-Observations about the effects*/
insert into Dictionary values ("135","pt","Transporte","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Corresponde aos efeitos do desastre sobre o setor de transporte: redes viárias (rodovias e linhas férreas), rodoviárias, aeroportos, pontes fluviais e marítimos, cais, etc., afetados e/ou destruídos."," "); /*135-en-Transport*/
insert into Dictionary values ("136","pt","Comunicações","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Corresponde aos efeitos do desastre sobre o setor de comunicações: sobre plantas e redes telefônicas, estações de rádio e televisão, correios e outros centros de informação pública, serviços de internet, radiotelefonia e comunicações celulares."," "); /*136-en-Communications*/
insert into Dictionary values ("137","pt","Instalações de socorro","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Corresponde aos efeitos do desastre sobre o setor de resposta à emergência, especificamente as instalações dos organismos de socorro: Bombeiros, pronto socorro e entidades de ordem pública."," "); /*137-en-Aid organisation installations*/
insert into Dictionary values ("138","pt","Agropecuário","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Corresponde aos efeitos do desastre sobre o setor Agropecuário: Campos de cultivos, fazendas, zonas de pastagens, etc."," "); /*138-en-Agriculture and fishing*/
insert into Dictionary values ("139","pt","Aqueduto","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Corresponde aos efeitos do desastre sobre o setor de Aqueduto: Poços, plantas de tratamento, aquedutos e canais de condução da água potável, tanques para armazenagem."," "); /*139-en-Aqueduct*/
insert into Dictionary values ("140","pt","Esgoto","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Corresponde aos efeitos do desastre sobre o setor de Esgoto: Redes de disposição de águas residuais e/ou pluviais e suas plantas de tratamento."," "); /*140-en-Sewerage*/
insert into Dictionary values ("141","pt","Educação","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Corresponde aos efeitos do desastre sobre o setor de Educação. Todo o relacionado com este setor: creches, escolas, colégios, universidades, centros de capacitação, bibliotecas, centros culturais, etc."," "); /*141-en-Education*/
insert into Dictionary values ("142","pt","Energia","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Corresponde aos efeitos do desastre sobre o setor energético: hidroelétricas, subestações, linhas de transmissão, plantas de geração, plantas de processamento e depósitos combustíveis, oleodutos, gasodutos, plantas nucleares."," "); /*142-en-Energy*/
insert into Dictionary values ("143","pt","Indústrias","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Corresponde aos efeitos do desastre sobre o setor industrial: Indústrias de todos os tipos e tamanhos, incluídas agrícolas e pecuárias."," "); /*143-en-Industry*/
insert into Dictionary values ("144","pt","Saúde","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Corresponde aos efeitos do desastre sobre o setor da saúde. Todo o relacionado com este setor, incluindo as redes de comunicação, de atenção de emergências (ambulâncias), centros de atenção, etc."," "); /*144-en-Health*/
insert into Dictionary values ("145","pt","Outros","Selecione uma opção","Campo qualitativo do banco de dados. Tem duas opções: Afetado e Não Afetado. Relaciona-se com os danos que não correspondem a nenhum dos campos do formulário básico."," "); /*145-en-Other*/
insert into Dictionary values ("146","pt","Rascunho","Selecione uma opção","Corresponde a um dos estados de armazenamento do formulário: Formulário em edição ainda não terminada (responsabilidade do Atualizador)"," "); /*146-en-Draft*/
insert into Dictionary values ("147","pt","Para aprovação","Selecione uma opção","Corresponde a um dos estados de armazenamento do formulário: Formulário que está na lista para ser aprovado (pelo supervisor)."," "); /*147-en-For approval*/
insert into Dictionary values ("148","pt","Eliminar","Selecione uma opção","Corresponde a um dos estados de armazenamento do formulário: Formulário que está na lista para ser eliminado (pelo supervisor)"," "); /*148-en-Delete*/
insert into Dictionary values ("149","pt","Publicada","Selecione uma opção","Corresponde a um dos estados de armazenamento do formulário: Formulário de consulta pública. Já foi aprovado pelo Supervisor para sua publicação."," "); /*149-en-Published*/
insert into Dictionary values ("150","pt","Eliminada!","Selecione uma opção","Corresponde a um dos estados de armazenamento do formulário: Formulário eliminado pelo Supervisor."," "); /*150-en-Deleted!*/
