insert into Dictionary values ("207","pt","Tipo de Gráfico","Selecione um tipo de gráfico","Os tipos de gráficos disponíveis são: histogramas temporais, estacionais e gráficos comparativos."," "); /*207-en-Type of Graphic*/
insert into Dictionary values ("208","pt","Temporal","Histograma de barras ou linhas, o eixo X corresponde ao tempo, o eixo Y corresponde ao efeito que vai se representar","Histograma temporal. Representação gráfica de uma variável dos efeitos em forma de barras ou linhas, onde a superfície de cada barra/linha corresponde à freqüência do valor representado (eixo vertical), segundo o período que o usuário selecione (eixo horizontal)."," "); /*208-en-Temporal*/
insert into Dictionary values ("209","pt","Evento/temporal","Igual que o histograma temporal, discrimina por eventos no eixo dos X.","Histograma evento/temporal. Representação gráfica de um ou mais eventos em termos de uma variável de efeitos na forma de barras ou linhas, onde a altura atingida pela barra/linha corresponde à freqüência do valor representado (eixo vertical), segundo o período de tempo (eixo horizontal) que o usuário selecione."," "); /*209-en-Type of event/Temporal*/
insert into Dictionary values ("210","pt","/temporal","Igual que o histograma temporal, descrimina por unidades geográficas no eixo dos X.","Histograma Geográfico/temporal: Representação gráfica de um a seis unidades geográficas em termos de uma variável de efeitos na forma de barras ou linhas, onde a altura atingida pela barra/linha corresponde à freqüência do valor representado (eixo vertical), segundo o período de tempo (eixo horizontal) que o usuário selecione."," "); /*210-en-/temporal*/
insert into Dictionary values ("211","pt","Causa/temporal","Descrimina por causas no eixo dos X.","Representação gráfica de uma a seis causas em termos de uma variável de efeitos na forma de barras ou linhas, onde a altura atingida pela barra/linha corresponde à freqüência do valor representado (eixo vertical), segundo o período de tempo (eixo horizontal) que o usuário selecione."," "); /*211-en-Type of cause/temporal*/
insert into Dictionary values ("212","pt","Por eventos","Compara os eventos num gráfico tipo pizza, segundo a variável a se representar","Comparativo de eventos: Gráfico comparativo - tipo pizza- de uma variável (de efeitos ou de número de relatos) agrupada pelos eventos selecionados"," "); /*212-en-By type of events*/
insert into Dictionary values ("213","pt","Por ","Compara as unidades geográficas num gráfico tipo pizza, segundo a variável a se representar","Comparativo de unidades geográficas: Gráfico comparativo - tipo pizza - de uma variável (de efeitos ou de número de relatos) agrupada pelas unidades geográficas selecionadas"," "); /*213-en-By*/
insert into Dictionary values ("214","pt","Por causas","Compara as causas num gráfico tipo pizza, segundo a variável a se representar","Comparativo de unidades geográficas: Gráfico comparativo - tipo pizza - de uma variável (de efeitos ou de número de reportes) agrupada pelas causas selecionadas"," "); /*214-en-By type of causes*/
insert into Dictionary values ("215","pt","Série","Série de dados a representar (mensal, diária)","Série histórica a ser representada. Por exemplo, a série pode ser diária ou mensal"," "); /*215-en-Series*/
insert into Dictionary values ("216","pt","Estacionariedade","Período estacional a ser analisado. Por exemplo, anual.","Período estacional ou estacionariedade de interesse. Por exemplo, anual ou mensal."," "); /*216-en-Seasonal*/
